mapper.insert:
INSERT INTO UserTicket (
  UserId, TicketId, Total, IsUsed, IsActive, CreateTime, UpdateTime
) VALUE (
  #{userId}, #{ticketId}, 1, 0, 1, (select UNIX_TIMESTAMP(NOW()) * 1000), (select UNIX_TIMESTAMP(NOW()) * 1000)
)

mapper.select:
SELECT count(id)
FROM UserTicket
WHERE isActive = 1 AND ticketId = #{id}
